import React from 'react';
import styles from './Billboard.module.css';
import billboard from './assets/billboard.webp';
import billboardtitle from './assets/billboard-title.webp';
import PlayIcon from './PlayIcon';
import InfoIcon from './InfoIcon';
const Billboard = () => {

    return (
        <div className={styles.billboard}>
            <div className={styles.innerbillboard}>
                <img src ={billboard} alt="The Blind Side" />
                <div className={styles.title}>
                    <div className={styles.info}>
                    <img src ={billboardtitle} alt="The Blind Side" />

                    <div className={styles.links}>
                    <a>
                        <PlayIcon /> 
                        <span>Play</span>

                    </a>

                    <button>
                    <InfoIcon />    
                        More Info </button>
                    </div>


                    </div>
                </div>
           </div>

        </div>
    );
};

export default Billboard;
